        this.getWeathers();
    },
    mounted: function mounted() {
        console.log('The Window Screen Height Is: ' + window.screen.height + 'px');
        document.getElementById('sessionone').style.height = window.screen.height + 'px';
        document.getElementById('sessiontwo').style.height = (window.screen.height - 40) + 'px';
        return window.addEventListener('scroll', this.handleScroll)
    },
    methods: {
        meta: function () { return ({
            title: 'iWeAther',
            description: '当前天气',
            keywords: 'PWA'
        }); },
        getWeathers: function () {
            var self = this
            self.$http.get('/now').then(function (response) {
                self.$data.now = response.data.HeWeather5[0].now;
                self.$data.basic = response.data.HeWeather5[0].basic;
            }).catch(function (error) {
                throw new Error(error);
            })

            self.$http.get('/forecast').then(function (response) {
                self.$data.daily_forecast = response.data.HeWeather5[0].daily_forecast;
            }).then(function () {
                self.calcuTems(self.$data.daily_forecast)
            }).then(function () {
                self.renderCharts();
            }).catch(function (error) {
                throw new Error(error);
            })
            console.log(self.$data)
        },
        calcuTems: function (temArr) {
            var ref = [[], [], []];
            var temMaxs = ref[0];
            var temMins = ref[1];
            var xDate = ref[2];
            temArr.forEach(function (item, i) {
                temMaxs.push(item.tmp.max);
                temMins.push(item.tmp.min);
                xDate.push(i == 0 ? '今天' : Number(item.date.split('-')[1]) + '-' + Number(item.date.split('-')[2]));

            })
            this.$data.afterCalculater = { maxTmps: temMaxs, minTmps: temMins, xDate: xDate };
            console.log(temMaxs, temMins);
        },
        renderCharts: function () {
            var self = this;
            var myChart = this.$echarts.init(document.getElementById('tem-week'));
            myChart.setOption({
                "xAxis": {
                    "type": 'category',
                    "boundaryGap": false,
                    "data": self.$data.afterCalculater.xDate
                },
                "yAxis": {
                    "show": false,
                    "type": 'value',
                    "min": 'dataMin',
                    "max": 'dataMin',
                    "axisLabel": {
                        "formatter": '{value} °C'
                    }
                },
                "series": [
                    {
                        "name": '最高气温',
                        "type": 'line',
                        "lineStyle": {
                            "normal": {
                                "width": 1,
                                "color": '#f04134',
                                "type": 'solid',
                                "shadowColor": 'rgba(0, 0, 0, 0.5)',
                                "shadowBlur": 10
                            }
                        },
                        "areaStyle": {
                            "normal": {
                                "color": '#f04134'
                            }
                        },
                        "smooth": true,
                        "data": self.$data.afterCalculater.maxTmps,
                        "markLine": {
                            "data": [
                                { "type": 'average', "name": '平均值' }
                            ]
                        }
                    }, {
                        "name": '最低气温',
                        "type": 'line',
                        "lineStyle": {
                            "normal": {
                                "width": 1,
                                "color": '#7265e6',
                                "type": 'solid',
                                "shadowColor": 'rgba(0, 0, 0, 0.5)',
                                "shadowBlur": 10
                            }
                        },
                        "areaStyle": {
                            "normal": {
                                "color": '#7265e6'
                            }
                        },
                        "smooth": true,
                        "data": self.$data.afterCalculater.minTmps,
                        "markLine": {
                            "data": [
                                { "type": 'average', "name": '平均值' } ]
                        }
                    }
                ]
            });
        },
        handleScroll: function (e) {
            // Do Some Thing
        }
    }
});


